mapper.insert:
INSERT INTO user_analysis(
    analysis_uuid, open_id, invitation_uuid, analysis_content, created_at, updated_at
) VALUES (
    #{analysisUuid}, #{openId}, #{invitationUuid}, #{analysisContent}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE user_analysis
SET analysis_content = #{analysisContent},
    updated_at = #{updatedAt}
WHERE id = #{id}
